mapper.select:
SELECT


			DATE_FORMAT(a.create_date,'${totalType}') as totalDate,




	count(*) as totalCount
FROM
	p_train a

	1=1


		AND a.id = #{id}


		AND a.begin_date = #{beginDate}


		AND a.train_num = #{trainNum}


		AND a.start_sta = #{startSta}


		AND a.arrival_sta = #{arrivalSta}


		AND a.arrival_date = #{arrivalDate}


		AND a.carriage_num = #{carriageNum}


		AND a.seat_num = #{seatNum}


		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.id_number = #{idNumber}


		AND a.bingli_id_number = #{bingliIdNumber}


		AND a.province = #{province}


		AND a.lx_tel = #{lxTel}


		AND a.tel_station = #{telStation}


		AND a.ptype = #{ptype}


		AND a.of_shizhou = #{ofShizhou}


		AND a.of_quxian = #{ofQuxian}


		AND a.resp_area = #{respArea}


		AND a.phone = #{phone}


		AND a.habitation = #{habitation}




		GROUP BY DATE_FORMAT(a.create_date,'${totalType}'),${groupBy}


		GROUP BY ${groupBy}


