mapper.select:
SELECT


			DATE_FORMAT(a.create_date,'${totalType}') as totalDate,




	count(*) as totalCount
FROM
	p_home a

	1=1


		AND a.id = #{id}


		AND a.sf_id = #{sfId}


		AND a.th_name LIKE
			'%'||#{thName}||'%'
			'%'+#{thName}+'%'
			concat('%',#{thName},'%')


		AND a.huhao = #{huhao}


		AND a.h_relation = #{hRelation}


		AND a.province = #{province}


		AND a.jc_date = #{jcDate}


		AND a.lx_tel = #{lxTel}


		AND a.tel_station = #{telStation}


		AND a.ptype = #{ptype}


		AND a.of_shizhou = #{ofShizhou}


		AND a.of_quxian = #{ofQuxian}


		AND a.resp_area = #{respArea}


		AND a.phone = #{phone}


		AND a.habitation = #{habitation}




		GROUP BY DATE_FORMAT(a.create_date,'${totalType}'),${groupBy}


		GROUP BY ${groupBy}


		GROUP BY DATE_FORMAT(a.create_date,'${totalType}')






		ORDER BY ${orderBy}
