mapper.select:
SELECT


			DATE_FORMAT(a.create_date,'${totalType}') as totalDate,




	count(*) as totalCount
FROM
	patients a

	1=1


		AND a.id = #{id}


		AND a.pname LIKE
			'%'||#{pname}||'%'
			'%'+#{pname}+'%'
			concat('%',#{pname},'%')


		AND a.ptel = #{ptel}


		AND a.of_shizhou = #{ofShizhou}


		AND a.sf_id = #{sfId}


		AND a.ptype = #{ptype}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}




		GROUP BY DATE_FORMAT(a.create_date,'${totalType}'),${groupBy}


		GROUP BY ${groupBy}


		GROUP BY DATE_FORMAT(a.create_date,'${totalType}')






		ORDER BY ${orderBy}
