mapper.update:
UPDATE p_hqaddr SET 	
	pid = #{pid},
	ptel = #{ptel},
	hq_addr = #{hqAddr},
	stay_hours = #{stayHours}
WHERE id = #{id}

UPDATE p_hqaddr SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT


			DATE_FORMAT(a.create_date,'${totalType}') as totalDate,




	count(*) as totalCount
FROM
	p_hqaddr a

	1=1


		AND a.id = #{id}


		AND a.pid = #{pid}


		AND a.ptel = #{ptel}


		AND a.hq_addr = #{hqAddr}


		AND a.stay_hours = #{stayHours}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}




		GROUP BY DATE_FORMAT(a.create_date,'${totalType}'),${groupBy}


		GROUP BY ${groupBy}


		GROUP BY DATE_FORMAT(a.create_date,'${totalType}')






		ORDER BY ${orderBy}
