com.mirth.connect.plugins.javascriptstep.script:
var messageData = sourceMap.get('parameters');
var cmd = messageData.getParameter("cmd");
var paramObj = messageData.getParameter("paramObj");
if(cmd == null){
	destinationSet.removeAllExcept(['html_list']);
}else if (cmd == "new"){
	destinationSet.removeAllExcept(['html_new']);
}else if (cmd == "new_srclist"){
	destinationSet.removeAllExcept(['html_new_srclist']);
	var src_usr = messageData.getParameter("src_usr");
	globalChannelMap.put('src_usr',src_usr);
	var src_pw = messageData.getParameter("src_pw");
	globalChannelMap.put('src_pw',src_pw);
	var src_jdbcurl = messageData.getParameter("src_jdbcurl");
	globalChannelMap.put('src_jdbcurl',src_jdbcurl);
	globalChannelMap.put('src_driverClass',getDriver(src_jdbcurl));
	var des_usr = messageData.getParameter("des_usr");
	globalChannelMap.put('des_usr',des_usr);
	var des_pw = messageData.getParameter("des_pw");
	globalChannelMap.put('des_pw',des_pw);
	var des_jdbcurl = messageData.getParameter("des_jdbcurl");
	globalChannelMap.put('des_jdbcurl',des_jdbcurl);
	globalChannelMap.put('des_driverClass',getDriver(des_jdbcurl));
	var creat_table = messageData.getParameter("creat_table");
	if(creat_table == null){
		globalChannelMap.put('creat_table',"no");
	}else if(creat_table == "on"){
		globalChannelMap.put('creat_table',"yes");
	}
}else if (cmd == "create" ){
	destinationSet.removeAllExcept(['data_new_create']);
	var obdata = JSON.parse(paramObj);
	var tablesmap = obdata.source.tablesmap;
	var source = obdata.source;
	channelMap.put('tablesmap',tablesmap);
}else if (cmd == "new_creat_over"){
	destinationSet.removeAllExcept(['data_new_creat_over']);
	var errormsg = messageData.getParameter("errormsg");
	globalChannelMap.put('errormsg',errormsg);
}else if (cmd == "del"){
	destinationSet.removeAllExcept(['data_del']);
	var task_id = messageData.getParameter("task_id");
	globalChannelMap.put('task_id',task_id);
}else if (cmd == "delover"){
	destinationSet.removeAllExcept(['data_del_over']);
	var delmsg = messageData.getParameter("delmsg");
	globalChannelMap.put('delmsg',delmsg);
}else if (cmd == "start"){
	destinationSet.removeAllExcept(['update_start_detail']);
	var start_task_id = messageData.getParameter("task_id");
	globalChannelMap.put('start_task_id',start_task_id);
}else if (cmd == "startupdate"){
	destinationSet.removeAllExcept(['update_data']);
	var update_task_id = messageData.getParameter("update_task_id");
	globalChannelMap.put('update_task_id',update_task_id);
}
function getDriver(jdbcurl){
	if(jdbcurl.indexOf("mysql") > -1){
		return "com.mysql.cj.jdbc.Driver";
	}
	if(jdbcurl.indexOf("oracle") > -1){
		return "oracle.jdbc.driver.OracleDriver";
	}
	if(jdbcurl.indexOf("postgresql") > -1){
		return "org.postgresql.Driver";
	}
	if(jdbcurl.indexOf("sqlserver") > -1){
		return "net.sourceforge.jtds.jdbc.Driver";
	}
}

