/**
	生成id
	@return {String} return description
*/
function createID() {
	var ustr = UUIDGenerator.getUUID();
	var dastr = DateUtil.getCurrentDate("yyyyMMddHHmmss");
	return ustr.substring(0,8).concat(dastr.substring(0,8)).concat(ustr.substring(14,18));
}

/**
	生成任务名字
	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function createName(dbconn) {
	try {
		var dbresult = dbconn.executeCachedQuery('select count(*) from schie_data');
		dbresult.next();
		var max = dbresult.getInt(1)+1;
		var str = "任务"+(dbresult.getInt(1)+1);
		return str;
	} catch (err) {
		logger.error("生成任务名字失败");
		logger.error(err);
	}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function updateData(des_jdbcurl,des_driverClass,des_usr,des_pw,src_res,table_name,sum){
	var desconn;
	try {
		errorMsg = '增量更新异常:目标数据库连接失败！<br>';
		desConn = DatabaseConnectionFactory.createDatabaseConnection(des_driverClass,des_jdbcurl, des_usr,des_pw);
		dconn = desConn.getConnection();
		desConn.setAutoCommit(false);
		des_statement = dconn.createStatement();

		var resultMetaData = src_res.getOriginal().getMetaData();
		var cols = resultMetaData.getColumnCount();
		errorMsg = '增量更新异常:更新新增数据失败！<br>';
		while(src_res.next()){
			var insert_SQL = 'INSERT INTO '+table_name+' (';
			var insert_SQL2 = ") VALUES (";
			for (var j = 1; j <= cols; j++) {
				insert_SQL += resultMetaData.getColumnName(j)+",";
				insert_SQL2 += "'"+src_res.getObject(j)+"',";
			}
			insert_SQL = insert_SQL.substring(0,insert_SQL.length-1) + insert_SQL2.substring(0,insert_SQL2.length-1) + ')';
			des_statement.addBatch(insert_SQL);
			sum++;
		}
		des_statement.executeBatch();
		desConn.commit();	 
		return sum; 
	} catch (err) {
		logger.info(errorMsg+err.toString());
		throw errorMsg+err;
		desConn.rollback();
		return;
	}
	finally {
		if (des_statement) des_statement.close();
		if (desConn) desConn.close();
	}
}
